try {
  // –ü–æ–ª—É—á–∞–µ–º –≤—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
  // –í —Å–ª—É—á–∞–µ, –µ—Å–ª–∏ $input –∏–º–µ–µ—Ç –º–µ—Ç–æ–¥ all(), –∏—Å–ø–æ–ª—å–∑—É–µ–º –µ–≥–æ, –µ—Å–ª–∏ –Ω–µ—Ç - –∏—Å–ø–æ–ª—å–∑—É–µ–º item()
  const inputData = $input.all ? $input.all() : [$input.item];

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –¥–∞–Ω–Ω—ã—Ö –∏ —Å—Ç—Ä—É–∫—Ç—É—Ä—É
  if (!inputData || inputData.length === 0) {
    throw new Error('–ù–µ—Ç –≤—Ö–æ–¥–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö');
  }

  // –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—Å—Ç —Å—Ç–∞—Ç—å–∏ –∏–∑ –ø–µ—Ä–≤–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞
  const inputText = inputData[0].json ? inputData[0].json.output : inputData[0].output;

  if (!inputText) {
    throw new Error('–ù–µ –Ω–∞–π–¥–µ–Ω —Ç–µ–∫—Å—Ç —Å—Ç–∞—Ç—å–∏ –≤ –ø–æ–ª–µ "output"');
  }

  // –ò–∑–≤–ª–µ–∫–∞–µ–º JSON –±–ª–æ–∫ —Å —Ç–æ–≤–∞—Ä–∞–º–∏
  let products = [];
  const jsonMatch = inputText.match(/```json\n([\s\S]*?)\n```/);

  if (jsonMatch && jsonMatch[1]) {
    try {
      const productsData = JSON.parse(jsonMatch[1]);
      products = productsData.products || [];
    } catch (e) {
      console.error('–û—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞ JSON:', e.message);
      products = [];
    }
  }

  // –£–¥–∞–ª—è–µ–º JSON –±–ª–æ–∫ –∏–∑ —Å—Ç–∞—Ç—å–∏
  const cleanArticle = inputText.replace(/```json\n[\s\S]*?\n```/, '').trim();

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –º–∞—Ä–∫–µ—Ä–æ–≤ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π
  const imageMarkerRegex = /\[IMAGE_\d+\]/g;
  const imageMarkers = cleanArticle.match(imageMarkerRegex) || [];

  // –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ –ø—Ä–µ–¥—ã–¥—É—â–µ–≥–æ —à–∞–≥–∞ "Parse & Validate Structure"
  // –ë–µ–∑–æ–ø–∞—Å–Ω–æ–µ –ø–æ–ª—É—á–µ–Ω–∏–µ —Å –ø—Ä–æ–≤–µ—Ä–∫–∞–º–∏
  let parseData = {};
  try {
    const parseStep = $('Parse & Validate Structure');
    if (parseStep && parseStep.first) {
      const firstItem = parseStep.first();
      if (firstItem && firstItem.json) {
        parseData = firstItem.json;
      }
    }
  } catch (e) {
    console.warn('–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å –¥–∞–Ω–Ω—ã–µ –∏–∑ Parse & Validate Structure:', e.message);
  }

  // –ü–æ–¥–≥–æ—Ç–∞–≤–ª–∏–≤–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
  const result = {
    json: {
      article_id: parseData.article_id || 'unknown',
      article_content: cleanArticle,
      products: products,
      image_markers: imageMarkers,
      images_plan: parseData.images_plan || [],
      structureData: parseData.structure || {},
      word_count: cleanArticle.split(/\s+/).length,
      timestamp: new Date().toISOString()
    }
  };

  return [result];

} catch (error) {
  console.error('–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∞—è –æ—à–∏–±–∫–∞ –≤ —Å–∫—Ä–∏–ø—Ç–µ:', error);
  return [{
